import unittest
from src.custom_json import obj2pretty_json
from src.lib import parse_session_report
from datetime import time
from tests.utils import get_file_path_in_same_folder


class SessionReport(unittest.TestCase):
    def test(self):
        file_path = get_file_path_in_same_folder(__file__, "01_d_ksiazka.pdf")
        obj = parse_session_report(file_path)
        # print(obj2pretty_json(obj))
        # exit()

        self.assertEqual(obj["term_number"], "X")
        self.assertEqual(obj["sitting_number"], "1")
        self.assertEqual(obj["sitting_day"], 4)
        self.assertEqual(obj["sitting_start_time"], None)
        self.assertEqual(obj["sitting_end_time"], None)
        self.assertEqual(obj["session_date"].isoformat(), "2023-11-22T00:00:00")
        self.assertEqual(obj["session_start_time"], time(11, 6))
        self.assertEqual(obj["session_end_time"], time(16, 35))
        self.assertEqual(
            obj["session_intra_breaks_times"],
            [
                {
                    "start": time(11, 14),
                    "end": time(11, 17),
                },
            ],
        )
        self.assertEqual(obj["speaker_senior"], None)
        self.assertEqual(obj["speaker"], None)
        self.assertEqual(obj["vicespeakers"], ["Piotr Zgorzelski", "Dorota Niedziela"])
